import { Router } from '@angular/router';
import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { PhoneVerifyService } from 'src/app/services/phone-verify.service';

export interface phoneNoVerify{
   phonenumber:string;
}

@Component({
  selector: 'app-phone-no-verify',
  templateUrl: './phone-no-verify.component.html',
  styleUrls: ['./phone-no-verify.component.css']
})
export class PhoneNoVerifyComponent {

  form= new FormGroup({
    phonenumber: new FormControl('',[Validators.required, Validators.pattern("^[0-9]{10}$")])
 });

  constructor(private phoneVerify: PhoneVerifyService, private router: Router){}



  onClick(data: string)
  {
    this.phoneVerify.setNumber(data);
    console.log(data);
    this.phoneVerify.postNumber(data,(retval:any)=>{
      console.log(retval);
      alert("Otp sent to your phone number successfully");
    });
  }
}
<div class="container">
    <h4>Enter your phone number for verification:</h4><br>
    <form [formGroup]="form" #f="ngForm">
        <div class="fg">
            <label for="phonenumber" class="form-label" >Phone Number</label>
              <input formControlName="phonenumber" type="text" class="form-control" id="phonenumber" name="phonenumber">
              <div *ngIf="(form.get('phonenumber').touched || form.get('phonenumber').dirty) && form.get('phonenumber').invalid">
                <div class="alert alert-danger" *ngIf="form.get('phonenumber').errors?.['required']">Phone Number is required</div>
                <div class="alert alert-danger" *ngIf="form.get('phonenumber').errors?.['pattern']">Enter only 10 digits of yourphone number.</div>
                </div>
            <br>
            <div>
                <button class="btn" type="button" routerLink="otpVerify" (click)="onClick(f.value)">Get OTP</button><br>
            </div>
        </div>
    </form>  
</div>
