import { Coursedetail } from './../../coursedetails/coursedetails.component';

import { HttpErrorResponse } from '@angular/common/http';
import { Component, EventEmitter, Output } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CourseServiceService } from 'src/app/services/course-service.service';
import { PhoneVerifyService } from 'src/app/services/phone-verify.service';
import { RegisterService } from 'src/app/services/register.service';
//import { RegisterForm } from '../register-form/register-form.component';

export interface Otp{
  otp: string;
}
export interface RegisteredCourse{
  id:number;
  name:string;
  description:string;
  duration:string;
  courseType:string;
  fees:number

}

export interface RegisterForm{
  name: string;
  phoneNumber: string;
  whatsappNumber: string;
  college: string;
  branch: string;
  semester: number;
  address: string;
  email: string;
  parentNumber:string;
  courses : RegisteredCourse[];
}

@Component({
  selector: 'app-otp-verify',
  templateUrl: './otp-verify.component.html',
  styleUrls: ['./otp-verify.component.css']
})
export class OtpVerifyComponent {
 // @Output() event = new EventEmitter<string>();
  form= new FormGroup({
    otp: new FormControl('',[Validators.required, Validators.minLength(6),Validators.maxLength(6)]),
    name:  new FormControl<any>(null,Validators.required),
    phoneNumber: new FormControl<any>(null,[Validators.required, Validators.pattern("^((\\+91-?)|0)?[0-9]{10}$")]),
    whatsappNumber: new FormControl(<any>null,Validators.required),
    college: new FormControl<any>(null,Validators.required),
    branch: new FormControl<any>(null,Validators.required),
    semester: new FormControl<any>(null,Validators.required),
    address: new FormControl<any>(null,Validators.required),
    parentNumber:new FormControl<any>(null,Validators.required),
    email: new FormControl<any>(null,[Validators.required,Validators.email]),
    courses:new FormGroup({
      id: new FormControl(),
      name:new FormControl()
    })
 });

 coursedetail : RegisteredCourse[]=[];
 notstudent:RegisteredCourse[]=[];

 otpview:boolean=true;
 registerform:RegisterForm={
   name: '',
   phoneNumber: '',
   whatsappNumber: '',
   college: '',
   branch: '',
   semester: 0,
   address: '',
   email: '',
   parentNumber: '',
   courses: []
 }
 registerview:boolean=false;

  constructor(private phoneVerify: PhoneVerifyService, private router: Router, private registerService:RegisterService,private courseservice:CourseServiceService){
    this.courseservice.getcourses((data:any)=>{
        this.coursedetail=data;
      })
  }

  
  public get otp(){
    return this.form.get('otp');
  }
  
  // check(retval:any):boolean{
  //   if(retval==="Valid otp!"){
  //     return true;
  //   }else{
  //     return false;
  //   }
  // }

  onClick(data: string)
  {
     let maindata:any = Object.assign(data,JSON.parse(this.phoneVerify.getNumber()));
     console.log(maindata);
     this.phoneVerify.postOtp(maindata).subscribe((response:any)=>{
        alert("Otp is validated")
        console.log(response);
       // alert("Otp is validated")
        this.registerform=response;
        this.studentcourse(response);
       // this.router.navigate(['phoneNumberVerify/otpVerify/registerForm']);
        this.phoneVerify.clear();
       // this.registerService.setData(response);
       // this.event.emit(this.registerService.getData());
        this.otpview=false;
        this.registerview=true;
      
     
    },
    (error : HttpErrorResponse)=>{
      // console.log(error);
      // alert("Otp is invalid");
      this.form.reset();
      if(error.status==404){
        console.log("error resolved")
        alert("Otp is validated")
        this.otpview=false;
        this.registerview=true;
      }
    });
        // if(this.check(retval)){
        //   alert("Phone number verified successfully");
        //   this.router.navigate(['registerForm']);
        // }else{
        //   alert("Invalid otp.Verify again");
        // }
        
   

    

    // this.phoneVerify.getData((data:any)=>{
    //    if(data!==null){  
    //       this.registerService.setData(data);
    //    }
    // });

  }
  // senddata(){
  //   //let data2=this.registerService.getData();
  //   this.event.emit(this.registerService.getData());
  // }

  onClick2()
  {
    console.log(JSON.parse(this.phoneVerify.getNumber()));
    this.phoneVerify.postNumber(JSON.parse(this.phoneVerify.getNumber()),(retval:any)=>{
      console.log(retval);
      alert("Otp sent to your phone number successfully");
    });
  }

  postdata(data:any){
    this.registerService.postregister(data).subscribe((response)=>{
      console.log(response);
     
      alert("data sent successfully");
      }),
      (error:HttpErrorResponse)=>{
        if(error.status==500){
          console.log("error resolved")
          alert("data sent successfully");
        }
      }
  
}

public get id(){
  return this.form.get('id');
 }
public get name(){
  return this.form.get('name');
}
public get description(){
  return this.form.get('description');
}
public get duration(){
  return this.form.get('duration');
}
public get courseType(){
  return this.form.get('courseType');
}
public get fees(){
  return this.form.get('fees');
}


public studentcourse(scourse){

  let k=0;
  for(let i=0;i<scourse.courses.length;i++)
  {
    for(let j=0;j<this.coursedetail.length;j++)
    {
      if(scourse.courses[i].id!==this.coursedetail[j].id)
      {
        this.notstudent[k]=this.coursedetail[j];
        k++;
      }
    }
  }

}
  



public onkeyup1(txt : HTMLInputElement) : void {
  txt.focus();
}
public onkeyup2(txt : HTMLInputElement) : void {
  txt.focus();
}
public onkeyup3(txt : HTMLInputElement) : void {
  txt.focus();
}
public onkeyup4(txt : HTMLInputElement) : void {
  txt.focus();
}
public onkeyup5(txt : HTMLInputElement) : void {
  txt.focus();
}

}
<div *ngIf="otpview" class="container">
    <!-- <h4>Enter your 6 digit code for verification:</h4><br>
    <form [formGroup]="form" #f="ngForm">
        <div class="fg">
            <label for="otp" class="form-label" >Enter OTP</label>
              <input formControlName="otp" type="text" class="form-control" id="otp" name="otp">
                <div class="alert alert-danger" *ngIf="form.get('otp').errors?.['minlength'] || form.get('otp').errors?.['maxlength']">Enter your 6 digit code.</div>
            <br>
            <div>
                <button class="btn" type="button" (click)="onClick(f.value)">Verify OTP</button><br><br>
                <small>
                    <a class="link" type="button" (click)="onClick2()">Resend OTP?</a>
                </small>
            </div>
        </div>
    </form>  -->


<form [formGroup]="form" #f="ngForm">
    <div class="container" style="margin-top: 70px; text-align: center; " >
      <h1>OTP</h1>
      <p>Enter 6 Digit Verification Code.</p>
      <hr>

     <div class="userInput">
        <input  type="text" 
                id='ist' 
                pattern="[0-9]*"
                maxlength="1" 
                #d1
                (keyup)="onkeyup1(d2)"
                >
        <input type="text" 
                id="sec"
                pattern="[0-9]*"
                maxlength="1" 
                #d2
                (keyup)="onkeyup2(d3)"
                >
        <input type="text" 
                id="third" 
                pattern="[0-9]*"
                maxlength="1" 
                #d3
                (keyup)="onkeyup2(d4)"
                >
        <input type="text" 
                id="fourth" 
                pattern="[0-9]*"
                maxlength="1" 
                #d4
                (keyup)="onkeyup2(d5)"
                >
        <input type="text" 
                id="fifth" 
                pattern="[0-9]*"
                maxlength="1"
                #d5 
                 (keyup)="onkeyup2(d6)"
                >
        <input type="text" 
                id="sixth" 
                pattern="[0-9]*"
                maxlength="1"
                #d6
                >
    </div>
      <hr>
      <p> <a href="#" (click)="onClick2()">Resend OTP</a>?</p>
      <button type="submit" class="Button1" (click)="onClick(f.value)">Verify OTP</button>
    </div>
  
 
  </form>
</div>


<div *ngIf="registerview" class="container" style="margin-top: 70px;width: 1300px;padding-left: 300px;" >
    <form [formGroup]="form" #f="ngForm" >
        <h3 class="text-center">Registration form</h3><br><br>
        <div class="fg">
            <label for="name" class="form-label" >Name</label>
            <input ngModel #RegisterName="ngModel" required maxlength="50" minlength="10" pattern="[A-Z,a-z]+" type="text" class="form-control" id="name"  name="name" value="{{registerform.name}}">
            <div class="alert alert-danger" *ngIf="RegisterName.invalid && RegisterName.touched">
                <div *ngIf="RegisterName.errors!['required']"><strong>RegisterName Is Required</strong></div> 
                <div *ngIf="RegisterName.errors!['minlength']">Name Should Be Min 10 Alphabets</div>
                <div *ngIf="RegisterName.errors!['pattern']">Name Should Contain Alphabets</div>
        </div>
        </div>
        <div class="fg">
            <label for="phoneNumber" class="form-label" >Phone Number</label>
            <input ngModel #Mobile="ngModel" required minlength="10" maxlength="10" pattern="[0-9]*"  type="text" class="form-control" id="phoneNumber"  name="phoneNumber" value="{{registerform.phoneNumber}}">
            <div class="alert alert-danger" *ngIf="Mobile.invalid && Mobile.touched">
                <div *ngIf="Mobile.errors!['required']"><strong>Number Is Required</strong> </div>
                <div *ngIf="Mobile.errors!['minlength']">Mobile Number Should Be Min 4 Digits</div>
                <div *ngIf="Mobile.errors!['pattern']">Mobile Number Should Have Only Digits</div>
                <div *ngIf="Mobile.errors!['maxlength']">Not More Than 10 Digits</div>
        </div>
        </div>
        <div class="fg">
            <label for="whatsappNumber" class="form-label" >WhatsApp Number</label>
            <input ngModel #Whatsapp="ngModel" required minlength="10" maxlength="10" pattern="[0-9]*" type="text" class="form-control" id="whatsappNumber"  name="whatsappNumber" value="{{registerform.whatsappNumber}}">
            <div class="alert alert-danger" *ngIf="Whatsapp.invalid && Whatsapp.touched"></div>
            <div *ngIf="Whatsapp.errors!['required']"><strong>Number Is Required</strong> </div>
            <div *ngIf="Whatsapp.errors!['minlength']">Mobile Number Should Be Min 4 Digits</div>
            <div *ngIf="Whatsapp.errors!['pattern']">Mobile Number Should Have Only Digits</div>
        </div>
        <div class="fg">
            <label for="college" class="form-label" >College</label>
            <input  type="text" class="form-control" id="college"  name="college" value="{{registerform.college}}">
        </div>
        <div class="fg">
            <label for="branch" class="form-label" >Branch</label>
            <input  type="text" class="form-control" id="branch"  name="branch" value="{{registerform.branch}}">
        </div>
        <div class="fg">
            <label for="semester" class="form-label" >Semester</label>
            <input  type="text" class="form-control" id="semester"  name="semester" value="{{registerform.semester}}">
        </div>
        <div class="fg">
            <label for="address" class="form-label" >Address</label>
            <!-- <input  type="text" class="form-control" id="address"  name="address" value="{{registerform.address}}"> -->
            <textarea input type="text"  name="address" id="address" cols="85" required></textarea>
        </div>
        <div class="fg">
            <label for="email" class="form-label" >Email</label>
            <input  type="text" class="form-control" id="email"  name="email" value="{{registerform.email}}">
        </div>
        <div class="fg">
            <label for="parentNumber" class="form-label" >Parent Number</label>
            <input ngModel #ParentNumber="ngModel" required minlength="10" maxlength="10" pattern="[0-9]*" type="text" class="form-control" id="parentNumber"  name="parentNumber" value="{{registerform.parentNumber}}">
            <div class="alert alert-danger" *ngIf="ParentNumber.invalid && ParentNumber.touched"></div>
            <div *ngIf="ParentNumber.errors!['required']"><strong>Number Is Required</strong> </div>
            <div *ngIf="ParentNumber.errors!['minlength']">Mobile Number Should Be Min 4 Digits</div>
            <div *ngIf="ParentNumber.errors!['pattern']">Mobile Number Should Have Only Digits</div>
        </div>
        <div formGroupName="courses">
            <select formControlName="id" name="id" id="id" class="form-control">
                <option>Courses</option>
                <option *ngFor="let b of notstudent" value="{{b.id}}">{{b.name}}</option>
            </select>
        </div>
        <br>
        <div>
            <button class="btn btn-primary" type="submit" (click)="postdata(f.value)">Submit</button><br>

        </div>
    </form>
</div>
