import { FormGroup, FormControl, Validators } from '@angular/forms';
import { CoursedetailsService } from './../../services/coursedetails.service';
import { Component } from '@angular/core';

export interface Coursedetail{
  id:number;
  name:string;
  description:string;
  duration:string;
  courseType:string;
  fees:number

}

@Component({
  selector: 'app-coursedetails',
  templateUrl: './coursedetails.component.html',
  styleUrls: ['./coursedetails.component.css']
})


export class CoursedetailsComponent {
  coursedetail : Coursedetail[]=[]
  view:boolean=false;

  form= new FormGroup({
    name: new FormControl('',Validators.required),
    description: new FormControl('',[Validators.required, Validators.pattern("^((\\+91-?)|0)?[0-9]{10}$")]),
   duration: new FormControl('',[Validators.required,Validators.email]),

 })


  constructor(private courseservice:CoursedetailsService){
    this.courseservice.getdata((data:any)=>
    {
      this.coursedetail=data;
    })
  }

  Onclick(data:any){
    this.courseservice.postdata((data:any)=>{
      console.log(data);
      this.courseservice.getdata((data:any)=>
      {
        this.coursedetail=data;
      })
    })
  }

  public addview()
  {
    this.view=true;
  }
  public closeview(){
    this.view=false;
  }
}
