import { Component } from '@angular/core';
import { AdmissionService } from 'src/app/services/admission.service';


export interface Admission{
  id:number;
  data:string;
  feesPaid:number;
  sid:number;
  bid:number;
  sname:string
  bname:string;
}

@Component({
  selector: 'app-admission',
  templateUrl: './admission.component.html',
  styleUrls: ['./admission.component.css']
})
export class AdmissionComponent {

  view:boolean=false;
  modifyview:boolean=false;

 admission:Admission[]=[];
 adetail:Admission={
   id: 0,
   data: '',
   feesPaid: 0,
   sid: 0,
   bid: 0,
   sname: '',
   bname: ''
  }


  constructor(private admissionservice:AdmissionService){
    this.admissionservice.getdata((data:any)=>
    {
      this.admission=data;
    })
  }

  Onclick(data:any){
    this.admissionservice.postdata((data:any)=>{
      console.log(data);
    })
  }

  OnModify(data:any){
    this.admissionservice.putdata((data:any)=>{
      console.log(data);
      this.admissionservice.getdata((data:any)=>
      {
        this.admission=data;
      })
    })
    this.view=false;
    this.modifyview=false;
  }

  Ondelete(id:any){
    this.admissionservice.deletedata(id,(data:any)=>{
      this.admissionservice.getdata((data:any)=>
      {
        this.admission=data;
      })
    })
  }
}
